"use strict"
var obj = $('.J_lazy'),
    timeout = 0;

function lazyLoadImg() {
    //Logger.info('loading');
    var stop = document.documentElement.scrollTop || document.body.scrollTop;
    var sheight = document.documentElement.clientHeight || document.body.clientHeight;

    $('.J_lazy').each(function(index) {
        if ($(this).offset().top < stop + sheight) {
            //$.log(index, 'Loading Img');
            $(this).removeClass('J_lazy');
            $(this).attr('src', $(this).attr('data-src'));
            $(this).css({'background':'none'});
        }
    })
}

function lazyLoadImage(){
    $(".J_lazyImg").each(function(){
        if($(this).offset().top < $(window).height() + $(window).scrollTop()){
            $(this).removeClass("J_lazyImg");
            $(this).attr("src", $(this).attr("data-src"));
            $(this).css({"background": "none"});
        }
    });
}

function  lazyLoadMoreImage(){
    $(".J_lazyImg").each(function(){
        var _this = $(this),
            this_top = _this.offset().top,
            scroll_top = $(window).scrollTop(),
            window_height = $(window).height();
        if(this_top < window_height + scroll_top){
            _this.addClass("loading");
        }
    });
    $(".J_lazyImg.loading").each(function(){
        var _this = $(this);
        _this.removeClass("J_lazyImg loading").attr("src", _this.attr("data-src")).css({"background": "none"});
    });

}

function eventHandler(fn, callback){

    $(".J_lazyLaodData").each(function(){
        if(($(window).height() + $(window).scrollTop()) + 30 > $(document).height()){
            $(this).removeClass("J_lazyLaodData");
            fn && fn(callback);
        }
    });

}

var loazyloadHander = {
  loadBackgroundImg: function () {
    obj.css({
      'background': 'url(data:image/gif;base64,) center no-repeat',
      'min-width': '50px',
      'min-height': '50px'
    });
    obj.attr('src', 'data:image/gif;base64,R0lGODlhAQABAIAAAP///wAAACH5BAEAAAAALAAAAAABAAEAAAICRAEAOw==');
    window.addEventListener('scroll', function () {
      clearTimeout(timeout);
      timeout = setTimeout(lazyLoadImg, 200);
    }, false);
    lazyLoadImg();
  },
  loadImg: function () {
    window.addEventListener('scroll', function () {
      clearTimeout(timeout);
      timeout = setTimeout(lazyLoadImg, 200);
    }, false);
    lazyLoadImg();
  },

  loadImage: function () {
    window.addEventListener("scroll", function () {
      clearTimeout(timeout);
      timeout = setTimeout(lazyLoadImage, 200);
    }, false);
    lazyLoadImage();
  },

  lazyLoadMoreImage: function () {
    window.addEventListener("scroll", function () {
      clearTimeout(timeout);
      timeout = setTimeout(lazyLoadMoreImage, 200);
    }, false);
    lazyLoadMoreImage();
  },

  scrollLazyEvent: function (timeout, fn, callback) {

    window.addEventListener("scroll", function () {
      clearTimeout(timeout);
      timeout = setTimeout(function () {
        eventHandler(fn, callback);
      }, 200);
    }, false);
    eventHandler(fn, callback);

  }
}

export default loazyloadHander;
